p = 1; sig = 8.854187817*10^(-12);; % constants that are still a mystery. V = @(r,theta)  min(max(p*sin(theta)*cos(phi)./(4*pi*sig*r.^2),-1),1); % Need thresholding here. npts = 1000; r = linspace(-1,1,npts)'; phi = linspace(0,2*pi, npts)';[R, Phi] = meshgrid(r,phi); E = V(R,Phi);  [X,Y] = pol2cart(Phi,R);contourf(X,Y,E)colorbarxlabel("EJE X");ylabel("EJE Y");zlabel("EJE Z");